method convert_map_key(inputs: map<nat, bool>, f: nat->nat) returns(r:map<nat, bool>)
  requires forall n1: nat, n2: nat :: n1 != n2 ==> f(n1) != f(n2)
  ensures forall k :: k in inputs <==> f(k) in r
  ensures forall k :: k in inputs ==> r[f(k)] == inputs[k]
{
  // Initialize r to empty map
  r := map[];

  // Iterate through each key in inputs
  var inputKeys := inputs.Keys;
  while inputKeys != {}
    // Loop invariant: 
    // - r only contains mappings for keys already processed from inputs
    // - For processed keys, r[f(k)] == inputs[k]
    invariant forall k :: k in r ==> exists k' :: k' in inputs && k == f(k')
    invariant forall k :: k in inputs && f(k) in r ==> r[f(k)] == inputs[k]
    invariant forall k :: k !in inputKeys ==> k in inputs ==> f(k) in r && r[f(k)] == inputs[k]
  {
    // Take a key from inputKeys
    var someKey :| someKey in inputKeys;

    // Add the mapping f(someKey) := inputs[someKey] to r
    r := r[f(someKey) := inputs[someKey]];

    // Remove processed key from inputKeys
    inputKeys := inputKeys - {someKey};
  }
}

