  requires x >= 0.0
  ensures r * r == x && r >= 0.0
{
  // Start with an initial guess of x/2
  r := x / 2.0;

  // Loop until the guess is close enough to the actual square root
  while (r*r - x > 0.001 || x - r*r > 0.001)
  invariant r >= 0.0
  invariant r*r >= x ==> (r - 0.001)*(r - 0.001) <= x
  invariant r*r < x ==> (r + 0.001)*(r + 0.001) >= x
