method Search( s: seq<int>, x: int ) returns ( k: int )
    // Ekki m breyta forskilyrum ea eftirskilyrum fallsins
    requires forall p,q | 0 <= p < q < |s| :: s[p] <= s[q];
    ensures 0 <= k <= |s|;
    ensures forall i | 0 <= i < k :: s[i] <= x;
    ensures forall i | k <= i < |s| :: s[i] >= x;
    ensures forall z | z in s[..k] :: z <= x;
    ensures forall z | z in s[k..] :: z >= x;
    ensures s == s[..k]+s[k..];
{
    // Setji vieigandi stofn fallsins hr.
    var p := 0;
    var q := |s|;

    if p == q
    {
        return p;
    }
    while p != q 
        invariant 0 <= p <= q <= |s|
        invariant forall i | 0 <= i < p :: s[i] <= x
        invariant forall i | q <= i < |s| :: s[i] >= x
        invariant s == s[..p] + s[p..q] + s[q..]
        decreases q - p
    {
        var m := p + (q-p)/2;
        if s[m] == x
        {
            return m;
        }
        if s[m] < x
        {
            p := m+1;
        }
        else
        {
            q := m;
        }
    }

    return p;
}

method Sort( m: multiset<int> ) returns ( r: seq<int> )
    ensures multiset(r) == m;
    ensures forall p,q | 0 <= p < q < |r| :: r[p] <= r[q];
{
    // Setji vieigandi frumstillingu  r og rest hr.
    // r er skilabreyta en rest er n breyta sem i bi til.
    r := [];
    var rest := m;
    while rest != multiset{}
        invariant multiset(r) + rest == m
        invariant forall p,q | 0 <= p < q < |r| :: r[p] <= r[q]
        // Ekki breyta fastayringu lykkju
        decreases |rest|
    {
        // Setji vieigandi stofn lykkjunnar hr.
        // Fjarlgi eitt gildi r rest me
        //    var x :| x in rest;
        //    rest := rest-multiset{x};
        // og noti Search til a finna rttan sta
        //  r til a stinga [x] inn  r.
        var x :| x in rest;
        rest := rest - multiset{x};
        var k := Search(r, x);
        r := r[..k] + [x] + r[k..];
    }
    return r;
}

