method MinOfMultiset( m: multiset<int> ) returns( min: int )
    requires m != multiset{};
    ensures min in m;
    ensures forall z | z in m :: min <= z;
{
    min :| min in m;
    var done := multiset{min};
    var m' := m-done;
    while m' != multiset{}
        invariant m == done + m';
        invariant min in done;
        invariant forall z | z in done :: min <= z;
        invariant forall z | z in m' :: min <= z;
        decreases m';
    {
        var z :| z in m';
        if z < min { 
            min := z; 
            done := multiset{min};
            m' := m - done;
        } else {
            done := done + multiset{z};
            m' := m' - multiset{z};
        }
    }
}

