  var z := 0;
  var a1: array<int> := new int[medium - low + 1];
  var a2: array<int> := new int[high - medium];
  // The first case
  while(y < medium - low + 1)
    invariant 0 <= y <= medium - low + 1
    invariant forall i :: 0 <= i < y ==> a1[i] == old(a[low+i])
  {
    a1[y] := a[low+y];
    y := y + 1;
  }
  // The second case
  while(z < high - medium)
    invariant 0 <= z <= high - medium
    invariant forall i :: 0 <= i < z ==> a2[i] == old(a[medium+i+1])
  {
    a2[z] := a[medium+z+1];
    z := z + 1;
  }
  y, z := 0, 0;
  // The third case
  while (x < high - low + 1)
    invariant 0 <= x <= high - low + 1
    invariant 0 <= y <= medium - low + 1
    invariant 0 <= z <= high - medium
    invariant forall i :: 0 <= i < x ==> 
                (i < y ==> a[low+i] == a1[i]) &&
                (i >= y && i-y < z ==> a[low+i] == a2[i-y])
    invariant forall i, j :: 0 <= i < j < y ==> a1[i] <= a1[j]
    invariant forall i, j :: 0 <= i < j < z ==> a2[i] <= a2[j]
    invariant y == medium - low + 1 ==> forall i :: y <= i < x && i-y < z ==> a[low+i] == a2[i-y]
    invariant z == high - medium ==> forall i :: 0 <= i < x && i < y ==> a[low+i] == a1[i]
    decreases high - low + 1 - x
  {
    if(y == medium - low + 1) {
      a[low+x] := a2[z];
      z := z+1;
    } else if(z == high - medium) {
      a[low+x] := a1[y];
