    // - a == 3^((n-k)/5)
    // - b == f(n) - a*f(k) 
    // - 0 <= k <= n
    while k >= 5
    invariant a == power(3, (n-k)/5)
    invariant b == f(n) - a*f(k)
    invariant 0 <= k <= n
    decreases k
    {
        // Assert loop invariant holds before each iteration
        assert a == power(3, (n-k)/5);
        assert b == f(n) - a*f(k);
        assert 0 <= k <= n;

        // Update b and a
        b := a*k + b;
        a := 3*a;

        // Decrement k by 5
        k := k - 5;
    }

    // Handle remaining iterations (0 <= k < 5)
    while k > 0 
    invariant 0 <= k < 5
    invariant b == f(n) - a*f(k)
    decreases k
    {
        // Update b
        b := b + a*k;

        // Decrement k
        k := k - 1;
    }

    // Assert postcondition holds after loop terminates
    assert k == 0;
    assert b == f(n) - a*f(0);

    x := b + a*0;
