method CardinalitySubsetLt<T>(A: set<T>, B: set<T>)
  requires A < B
  ensures |A| < |B|
{
  var b :| b in B && b !in A;
  var B' := B - {b};
  if A < B' {
    CardinalitySubsetLt(A, B');
  } else {
    assert A == B';  // A is a strict subset of B', so |A| < |B'| < |B|
  }
}

method strategy<T>(P: set<T>, Special: T) returns (count: int)
  requires |P| > 1 && Special in P
  ensures count == |P| - 1
{
  count := 0;
  var I := {};
  var S := {};
  var switch := false;

  while count < |P| - 1
    invariant I <= P && S <= P
    invariant Special in I ==> switch == false
    invariant Special !in I ==> switch == true || Special in S
    invariant S <= I
    invariant count == |I - {Special}|
    invariant count < |P| - 1 ==> |I| < |P|
  { 
    var prevI := I;
    var p :| p in P && p !in I;
    I := I + {p};

    if p == Special {
      if switch {
        switch := false;
        count := count + 1;
      }
      // Special has been added to I, so switch should be false
      assert switch == false;
    } else {
      if p !in S && !switch {
        S := S + {p};
        switch := true;
      }
      // p is not special, so switch should be true or Special is in S
      assert switch == true || Special in S;
    }
    // I has increased in size by 1
    assert |I| == |prevI| + 1;
    // Update the loop invariant
    if p == Special {
      assert count == |prevI - {Special}| + 1 == |I - {Special}|;
    } else {
      assert count == |prevI - {Special}| == |I - {Special}|;
    }
  }  

  // After the loop, count == |P| - 1
  assert count == |P| - 1;
  // S is a subset of I
  CardinalitySubsetLt(S, I);

  if I < P {
    // I is a strict subset of P
    CardinalitySubsetLt(I, P);
  } else {
    // I == P since I <= P (loop invariant) and |I| == |P| (loop terminates when count == |P| - 1)
    assert I == P;
  }
}

