method bubbleSort (a: array<int>)
  requires a != null && a.Length > 0;
  modifies a;
  ensures sorted(a, 0, a.Length);
  ensures multiset(a[..]) == multiset(old(a[..]));
{
  var i:nat := 1;

  while (i < a.Length)
    invariant 1 <= i <= a.Length;
    invariant sorted(a, 0, i);
    invariant multiset(a[..]) == multiset(old(a[..]));
  {
    ghost var a_old := a[..];
    var j:nat := i;
    while (j > 0)
      invariant 0 <= j <= i;
      invariant sorted(a, 0, i);
      invariant j < i ==> pivot(a, i, j);
      invariant multiset(a[..]) == multiset(a_old);
    {
      if (a[j-1] > a[j]) {
        a[j-1], a[j] := a[j], a[j-1];
      }
      j := j - 1;
    }
    assert sorted(a, 0, i) && pivot(a, i, 0);
    assert sorted(a, 0, i) && a[i-1] <= a[i];
    assert sorted(a, 0, i) && (forall k :: i <= k < i+1 ==> a[i-1] <= a[k]);
    assert sorted(a, 0, i+1);
    i := i+1;
  }
}

