method StringSwap(s: string, i:nat, j:nat) returns (t: string)
requires i >= 0 && j >= 0 && |s| >= 0;
requires |s| > 0 ==> i < |s| && j < |s|;
ensures multiset(s[..]) == multiset(t[..]);
ensures |s| == |t|;
ensures |s| > 0 ==> forall k:nat :: k != i && k != j && k < |s| ==> t[k] == s[k]
ensures |s| > 0 ==> t[i] == s[j] && t[j] == s[i];
ensures |s| == 0 ==> t == s;
{
    t := s;
    if |s| == 0 {
        // Empty string, no swapping needed
        return t;
    }
    assert i < |s| && j < |s|; // Precondition
    assert t == s; // t is initially equal to s

    var temp := s[i];
    t := t[i := s[j]];
    assert t[i] == s[j]; // t[i] is now equal to s[j]
    assert |t| == |s|; // Length is unchanged
    assert forall k:nat :: k != i && k < |t| ==> t[k] == s[k]; // Other elements are unchanged
    assert multiset(s[..i]) + multiset{s[j]} + multiset(s[i+1..]) == multiset(t[..i]) + multiset{t[i]} + multiset(t[i+1..]); // Multiset is unchanged

    t := t[j := temp];
