    assert forall k:nat :: k != i && k != j && k < |t| ==> t[k] == s[k]; // Other elements are unchanged
    assert multiset(s[..]) == multiset(t[..]); // Multiset is unchanged
    assert t[i] == s[j] && t[j] == s[i]; // i and j elements are swapped
