lemma {:induction false} Divby2(n: nat)
ensures (n*(n-1))%2 == 0
{
    if n == 0 {
        // n = 0
        // n*(n-1) = 0*(-1) = 0
        // 0%2 == 0
        assert (n*(n-1))%2 == 0;
    } else {
        // Inductive case: Assume true for n-1, prove for n
        // Inductive hypothesis: (n-1)*((n-1)-1) % 2 == 0
        Divby2(n - 1); // proved in case n - 1

        // (n-1)*((n-1)-1) = (n-1)*(n-2)
        //                 = n^2 - 3n + 2
        //                 = n(n-1) - 2(n-1)
        // Since (n-1)*(n-2) is even (by inductive hypothesis),
        // and 2(n-1) is even, their difference n(n-1) is also even
        assert (n-1)*((n-1)-1) == n*(n-1) - 2*(n-1);
        assert 2*(n-1) % 2 == 0;
        assert (n*(n-1) - 2*(n-1)) % 2 == 0;
        assert n*(n-1) % 2 == 0;
    }
}

