module Utils {

    lemma AllBelowBoundSize(bound: nat)
        ensures
            var below := set n : nat | n < bound :: n;
            |below| ==  bound
    {
        if bound == 0 {
            // Base case: empty set has size 0
            assert set n : nat | n < 0 :: n == {};
        } else {
            // Inductive case: split into bound-1 and the last element
            AllBelowBoundSize(bound-1);
            var belowminus := set n : nat | n < bound-1 :: n;
            var below := set n : nat | n < bound :: n;
            // belowminus has size bound-1 by induction hypothesis
            assert |belowminus| == bound-1;
            // below is belowminus plus the element bound-1
            assert below == belowminus + {bound-1};
            // the two sets are disjoint
            assert forall n :: n in belowminus ==> n !in {bound-1};
            assert forall n :: n in {bound-1} ==> n !in belowminus;
            // therefore, the size of below is the sum of the sizes
            SetSizes(belowminus, {bound-1}, below);
            assert |below| == |belowminus| + |{bound-1}|;
            assert |{bound-1}| == 1;
        }
    }

    lemma SizeOfContainedSet(a: set<nat>, b: set<nat>)
        requires forall n: nat :: n in a ==> n in b
        ensures |a| <= |b|
    {
        if |a| == 0 {
            // Base case: empty set is contained in any set
        } else {
            // Inductive case: remove one element from both sets
            var y :| y in a;
            var new_a := a - {y};
            var new_b := b - {y};
            // new_a is contained in new_b
            assert forall n: nat :: n in new_a ==> n in new_b;
            // apply induction hypothesis
            SizeOfContainedSet(new_a, new_b);
            // sizes of new_a and new_b are one less than a and b
            assert |new_a| == |a| - 1;
            assert |new_b| == |b| - 1;
        }
    }

    lemma BoundedSetSize(bound: nat, values: set<nat>)
        requires forall n :: n in values ==> n < bound
        ensures |values| <= bound
    {
        var all_below_bound := set n : nat | n < bound :: n;
        AllBelowBoundSize(bound);
        // values is contained in all_below_bound
        assert forall n :: n in values ==> n in all_below_bound;
        SizeOfContainedSet(values, all_below_bound);
    }

    lemma MappedSetSize<T, U>(s: set<T>, f: T->U, t: set<U>)
        requires forall n: T, m: T :: m != n ==> f(n) != f(m)
        requires t == set n | n in s :: f(n)
        ensures |s| == |t|
    {
        if |s| == 0 {
            // Base case: empty set maps to empty set
            assert t == {};
        } else {
            // Inductive case: remove one element from both sets
            var y :| y in s;
            var new_s := s - {y};
            var new_t := t - {f(y)};
            // new_t is the image of new_s under f
            assert new_t == set n | n in new_s :: f(n);
            // f is still injective on new_s
            assert forall n: T, m: T :: n in new_s && m in new_s && m != n ==> f(n) != f(m);
            // apply induction hypothesis
            MappedSetSize(new_s, f, new_t);
        }

