    assert s == power(a,i) && i<=n && i<n;
    /* (| s*a = power(a,i+1) ^ i+1<=n |)  - implied (b) */   
    assert s*a == power(a,i+1) && i+1<=n by {
      assert power(a,i+1) == a * power(a,i);  // by definition of power
    }
    s := s*a;
    /* (| s = power(a,i+1) ^ i+1<=n |)    - assignment */    
    assert s == power(a,i+1) && i+1<=n;
    i := i+1;
    /* (| s = power(a,i) ^ i<=n |)        - assignment */    
    assert s == power(a,i) && i<=n;
  }
  /* (| s = power(a,i) ^ i<=n ^ -(i<n) |) - partial-while */ 
  assert s == power(a,i) && i<=n && !(i<n);
  /* (| s = power(a,n) |)                 - implied (c) //Post-Condition: ensures statement above */
  assert i == n;  // loop terminates when i == n
}

/* Proof of implied (a): Follows from definition of the power function. */

/* Proof of implied (b): By definition of power, power(a,i+1) == a * power(a,i). */

/* Proof of implied (c): When loop terminates, i==n. Substituting in the loop invariant s == power(a,i) 
   gives s == power(a,n) */

/* Proof of termination: the loop guard gives us the expression i<n. This is equivalent to n-i>0.
   Prior to the loop, n>=0 and i=0.
   Each iteration of the loop, i increases by 1 and thus n-i decreases by 1. Thus n-i will eventually reach 0.
   When the n-i=0, n=i and thus the loop guard ends the loop as it is no longer the case that i<n.
   Thus the program terminates.
*/

