method single(x:array<int>, y:array<int>) returns (b:array<int>) 
requires x.Length > 0
requires y.Length > 0
// ensuring that the new array is the two arrays joined
ensures b[..] == x[..] + y[..]

{
    // getting the new array to have the length of the two arrays
    b:= new int [x.Length + y.Length];
    var i := 0;
    // to loop over the final array
    var index := 0;
    var sumi := x.Length + y.Length;

    while (i < x.Length && index < sumi) 
    invariant 0 <= i <= x.Length
    invariant 0 <= index <= sumi
    invariant index == i
    invariant forall j :: 0 <= j < i ==> b[j] == x[j]
    {
        b[index]:= x[i];
        i := i + 1;
        index:= index+1;
    }

    assert i == x.Length;
    assert index == x.Length;
    assert b[..x.Length] == x[..];

    i := 0;

    while (i < y.Length && index < sumi)
    invariant x.Length <= index <= sumi
    invariant 0 <= i <= y.Length 
    invariant index == x.Length + i
    invariant b[..x.Length] == x[..]
    invariant forall j :: 0 <= j < i ==> b[x.Length + j] == y[j]
    {
        b[index]:= y[i];
        i := i + 1;
        index:= index + 1;
    }

    assert i == y.Length;
    assert index == sumi;
    assert b[..] == x[..] + y[..];
}

