// the same, except that these languages do not check
// for any kind of termination.  Also, in Spec#, there
// is an issue of potential overflows.

// Benchmark1

method Add(x: int, y: int) returns (r: int)
  ensures r == x+y;
{
  r := x;
  if (y < 0) {
    var n := y;
    while (n != 0)
      invariant r == x+y-n;
    {
      r := r - 1;
      n := n + 1;
    }
  } else {
    var n := y;
    while (n != 0) 
      invariant r == x+y-n;
    {
      r := r + 1;
      n := n - 1;
    }
  }
}

method Mul(x: int, y: int) returns (r: int)
  ensures r == x*y;
  decreases if x < 0 then -x else x;
{
  if (x == 0) {
    r := 0;
  } else if (x < 0) {
    var posX := -x;
    r := Mul(posX, y);
    r := -r;
  } else {
    r := Mul(x-1, y);
    assert r == (x-1)*y; // from postcondition of recursive call
    r := Add(r, y);
    assert r == (x-1)*y + y; // from postcondition of Add
    // (x-1)*y + y == x*y
  }
}

