        Bind(Cons(x, m'), Return);
        append(Return(x), Bind(m', Return));
        { AppendNil(Return(x)); }
        Cons(x, Bind(m', Return));
        { assert Bind(m', Return) == m'; }
        Cons(x, m');
      }
  }
}

