}

method OneShot(n: int) returns (k: int)
  ensures k == n
{
  var f := (x: int) => x;
  var i := 0;
  while i < n
    invariant 0 <= i <= n
    invariant forall x :: 0 <= x < i ==> f.requires(x) && f(x) == x + 1
    invariant forall x :: i <= x ==> f.requires(x) && f(x) == x
