    invariant forall m,n :: 0 <= m < n < i ==> (!a[m] || !a[n])
    invariant forall m,n :: j < m < n < a.Length ==> (a[m] || a[n])
    invariant multiset(a[..]) == old(multiset(a[..]))
  {
    if (!a[i]) {
      i := i+1;
    } else if (a[j]) {
      j := j-1;
    } else {
      swap(a, i, j);
      i := i+1;
      j := j-1;
    }
