method FindMedian(a: array<int>, b: array<int>) returns (median: int)
    requires a != null && b != null
    requires a.Length == b.Length
    requires a.Length > 0
    requires forall i :: 0 <= i < a.Length - 1 ==> a[i] <= a[i + 1]
    requires forall i :: 0 <= i < b.Length - 1 ==> b[i] <= b[i + 1]
    ensures median == if (a.Length % 2 == 0) then (a[a.Length / 2 - 1] + b[0]) / 2 else a[a.Length / 2]
{
    // Since a and b are sorted arrays of the same length, 
    // if the length is odd, the median is the middle element of array a.
    // If the length is even, the median is the average of the 
    // middle element of array a and the first element of array b.

    if (a.Length % 2 == 0) 
    {
        assert a.Length >= 2; // Length is even, so it must be at least 2
        assert 0 <= a.Length / 2 - 1 < a.Length; // Bounds check for a[a.Length / 2 - 1]
        assert 0 <= 0 < b.Length; // Bounds check for b[0]

        median := (a[a.Length / 2 - 1] + b[0]) / 2;
    } 
    else 
    {
        assert a.Length >= 1; // Length is odd, so it must be at least 1
        assert 0 <= a.Length / 2 < a.Length; // Bounds check for a[a.Length / 2]

        median := a[a.Length / 2];
    }
}

