method ComputeFib(n: nat) returns (b: nat)
	ensures b == fib(n)
{
	var i: int := 1;
	if 0 <= n < 2 { return n; }
	b := 1;
	var c := 1;

	// loop invariant
	assert b == fib(i) && c == fib(i+1);
	while i < n
		invariant 1 <= i <= n
		invariant b == fib(i) && c == fib(i+1)
	{
		b, c := c, b + c;
		i := i + 1;
		// assert to prove loop invariant holds
		assert b == fib(i-1) + fib(i-2);
	}
	// assert to prove postcondition based on loop invariant
	assert i == n && b == fib(i) && c == fib(i+1);
}

method Main()
