method mySqrt(x: int) returns (res: int)
requires 0 <= x;
ensures sqrt(x, res);
{
    var l, r := 0, x;
    while (l <= r)
    invariant 0 <= l <= r + 1
    invariant forall i :: 0 <= i < l ==> i * i <= x
    invariant forall i :: r < i <= x ==> i * i > x
    {
        var mid := (l + r) / 2;
        if (mid * mid <= x && (mid + 1) * (mid + 1) > x) {
            res := mid;
            assert sqrt(x, res);
            return;
        } else if (mid * mid <= x) {
            l := mid + 1;
        } else {
            r := mid - 1;
        }
    }
    // The following assertion proves the postcondition in the case where the loop terminates
    // due to l > r, which implies that there is no integer square root for x
    assert l == r + 1 && l * l > x && (l - 1) * (l - 1) <= x;
    assert sqrt(x, l - 1);
    res := l - 1;
