method ClimbStairs(n: nat) returns (r: nat)
  ensures r == Stairs(n)
{
  if n <= 1 {
    return 1;
  } else {
    var a, b := 1, 1;
    var i := 1;
    while i < n
      invariant 1 <= i <= n
      invariant a == Stairs(i - 1)
      invariant b == Stairs(i)
    {
      a, b := b, a + b;
      i := i + 1;
    }
    return b;
  }
}

