function Fibonacci(n: nat): nat {
  match n {
    case 0 => 0
    case 1 => 1
    case _ => Fibonacci(n - 1) + Fibonacci(n - 2)
  }
}

// iterative calculation of Fibonacci numbers
method FibonacciIterative(n: nat) returns (f: nat)
  ensures f == Fibonacci(n)
{
  if n < 2 {
    return n;
  }

  var prev := 0;
  f := 1;
  var i := 1;

  while i < n 
    invariant 1 <= i <= n
    invariant f == Fibonacci(i)
    invariant prev == Fibonacci(i - 1)
  {
    assert Fibonacci(i + 1) == Fibonacci(i) + Fibonacci(i - 1); // by definition of Fibonacci
    prev, f := f, f + prev;
    i := i + 1;
  }
  assert i == n; // loop terminates when i == n
  assert f == Fibonacci(n); // by loop invariant, ensuring postcondition
}

