type Id(==)

function F(s: set<Id>): int

lemma Test(x: Id)
{
  var X := {x};
  var a := map i | i <= X :: F(i);
  var b := map[{} := F({}), X := F(X)];

  // Prove that a and b have the same keys
  forall i
    ensures i in a.Keys <==> i in b.Keys
  {
    calc {
      i in a.Keys;
    ==  { 
          // i is in a.Keys if and only if i is a subset of X
          assert i in a.Keys <==> i <= X; 
        }
      i <= X;
    ==  { 
          // i is a subset of X if and only if i is either {} or X
          assert i <= X <==> i == {} || i == X; 
        }
      i == {} || i == X;
    ==  {
          // {} and X are the keys in b
          assert b.Keys == {{}, X};
          // If i is either {} or X, then it is in b.Keys
          if i == {} {
            assert i in b.Keys;
          } else if i == X {
            assert i in b.Keys;
          }
        }  
      i in b.Keys;
    }
  }
}

