method FindMax(a: array<int>) returns (i: int)
    // Annotate this method with pre- and postconditions
    // that ensure it behaves as described.
    requires 0 < a.Length
    ensures 0 <= i < a.Length
    ensures forall k: int :: 0 <= k < a.Length ==> a[k] <= a[i]
{
    // Fill in the body that calculates the INDEX of the maximum.
    var j := 0;
    var max := a[0];
    i := 1;

    // Loop invariant:
    // - 0 <= j < i <= a.Length
    // - max is the maximum value in a[0..i]
    // - j is the index of max in a[0..i]
    while i < a.Length
        invariant 0 <= j < i <= a.Length
        invariant forall k: int :: 0 <= k < i ==> a[k] <= max
        invariant 0 <= j < i && max == a[j]
    {
        if max < a[i] {
            max := a[i];
            j := i;
        }
        i := i + 1;
    }

    // Assert that max is the maximum value in the entire array
    assert forall k: int :: 0 <= k < a.Length ==> a[k] <= max;
    // Assert that j is a valid index and contains the maximum value
    assert 0 <= j < a.Length && max == a[j];

    i := j;
}

