    var e :| e in a;
    assert e in b;
    assert a - {e} <= b - {e};
    if e in b {
      subsetCardinality(a - {e}, b - {e});
      assert |a - {e}| <= |b - {e}|;
      assert |a| == |a - {e}| + 1;
      assert |b| == |b - {e}| + 1;
    } else {
      assert false; // Cannot happen since a <= b
