method suma_componentes(V : array?<int>) returns (suma : int)

  requires V != null
  ensures  suma == suma_aux(V, 0)	// x = V[0] + V[1] + ... + V[N - 1]

{
  var n : int ;

  n := V.Length ;  // n := N
  suma := 0 ;

  assert suma == suma_aux(V, n);

  while n != 0
    invariant 0 <= n <= V.Length
    invariant suma == suma_aux(V, n)
  {
    assert suma == suma_aux(V, n);
    assert V[n-1] + suma_aux(V, n) == suma_aux(V, n-1);

    suma := suma + V[n - 1] ;

    assert suma == V[n-1] + suma_aux(V, n);
    assert suma == suma_aux(V, n-1);

    n := n - 1 ;

    assert suma == suma_aux(V, n);
  }

  assert n == 0;
  assert suma == suma_aux(V, 0);
