method DPGD_GradientPerturbation (size:int, learning_rate:real, noise_scale:real, gradient_norm_bound:real, iterations:int) returns (Para:real, PrivacyLost:real)
  requires iterations >= 0
  requires size >= 0
  requires noise_scale >= 1.0
  requires -1.0 <= gradient_norm_bound <= 1.0
{
  var thetha: array<real> := new real[iterations+1];
  thetha[0] := *;
  var alpha: real := 0.0;
  var tau: real := *;
  assume tau >= 0.0;
  var t: int := 0;

  while (t < iterations)
    invariant 0 <= t <= iterations
    invariant thetha.Length == iterations + 1
    invariant forall k :: 0 <= k < t ==> thetha[k+1] == thetha[k] - learning_rate * (sum({j in 0..size-1} | thetha[j] + noise_scale))  // Placeholder for actual gradient and noise computation
  {
    var i: int := 0;
    var beta: real := 0.0;
    var summation_gradient: real := 0.0;

    while (i < size)
      invariant 0 <= i <= size
      invariant beta == i * tau
      invariant summation_gradient == sum({j in 0..i-1} | thetha[j] + noise_scale)  // Placeholder for actual gradient and noise computation
    {
      var gradient: real := *;
      assume -gradient_norm_bound <= gradient <= gradient_norm_bound;
      var eta: real := *;
      assume -noise_scale <= eta <= noise_scale;
      beta := beta + tau;
      summation_gradient := summation_gradient + gradient + eta;
      i := i + 1;
    }

    alpha := alpha + beta;
    thetha[t+1] := thetha[t] - learning_rate * summation_gradient;
    t := t + 1;
  }

  Para := thetha[iterations];
  PrivacyLost := alpha;
}

