method CountLessThan(numbers: set<int>, threshold: int) returns (count: int)
  ensures count == |set i | i in numbers && i < threshold|
  ensures count == |SetLessThan(numbers, threshold)|
{
  count := 0;
  var ss := numbers;
  while ss != {}
    decreases |ss|
    invariant count + |SetLessThan(ss, threshold)| == |SetLessThan(numbers, threshold)|
    invariant forall x :: x in ss ==> x in numbers
    invariant forall x :: x in ss ==> x >= threshold || x in SetLessThan(ss, threshold)
  {
    var i: int :| i in ss;
    ss := ss - {i};
    if i < threshold {
      count := count + 1;
    }
  }
  assert count == |SetLessThan(numbers, threshold)|;
}

function SetLessThan(numbers: set<int>, threshold: int): set<int>
{
  set i | i in numbers && i < threshold
}

method Main()
{
  var s: set<int> := {1, 2, 3, 4, 5};
  var c: int := CountLessThan(s, 4);
  print c;
  assert c == 3;
}

