// This method should return true iff pre is a prefix of str. That is, str starts with pre
method isPrefix(pre: string, str: string) returns (res: bool)
    requires 0 < |pre| <= |str|
{
    var i := 0;
    while (i < |pre|)
        invariant 0 <= i <= |pre|
        invariant forall j :: 0 <= j < i ==> pre[j] == str[j]
    {
        if (str[i] != pre[i]) {
            return false;
        }
        i := i + 1;
    }
    return true;
}

// This method should return true iff sub is a substring of str. That is, str contains sub
method isSubstring(sub: string, str: string) returns (res: bool)
    requires 0 < |sub| <= |str|
{
    var i := 0;
    var n := |str| - |sub|;
    while (i <= n)
        invariant 0 <= i <= n
        invariant forall j :: 0 <= j < i ==> !isPrefix(sub, str[j..])
    {
        var prefixResult := isPrefix(sub, str[i..]);
        if (prefixResult) {
            return true;
        }
        i := i + 1;
    }
    return false;
}

// This method should return true iff str1 and str1 have a common substring of length k
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
    requires 0 < k <= |str1| && 0 < k <= |str2|
{
    var i := 0;
    var n := |str1| - k;
    while (i <= n)
        invariant 0 <= i <= n
        invariant forall j :: 0 <= j < i ==> !isSubstring(str1[j..j+k], str2)
    {
        var substringResult := isSubstring(str1[i..i+k], str2);
        if (substringResult) {
            return true;
        }
        i := i + 1;
    }
    return false;
}

// This method should return the natural number len which is equal to the length of the longest common substring of str1 and str2.
method maxCommonSubstringLength(str1: string, str2: string) returns (len: nat)
    requires 0 < |str1| && 0 < |str2|
{
    var maxLen := 0;
    var minLength := |str1|;
    if (|str2| < minLength) {
        minLength := |str2|;
    }
    var k := minLength;
    while (k > 0)
        invariant 0 <= k <= minLength
        invariant forall j :: k < j <= minLength ==> !haveCommonKSubstring(j, str1, str2)
    {
        var commonSubstringResult := haveCommonKSubstring(k, str1, str2);
        if (commonSubstringResult) {
            return k;
        }
        k := k - 1;
    }
    return 0;
}

