method sqrt(x: real) returns (r: real)
  requires x >= 0.0
  ensures r * r == x && r >= 0.0
{
  r := 0.0;
  var upper := x + 1.0;
  while (r * r != x)
    decreases upper - r
    invariant 0.0 <= r && r <= upper
    invariant r * r <= x && upper * upper >= x
  {
    var mid := (r + upper) / 2.0;
    if (mid * mid > x) {
      upper := mid;
    } else {
      r := mid;
    }
  }
}

