  assert r * r == 4.0; // Corrected to match the postcondition of sqrt
  if (r < 2.0) { 
    assert 0.0 < r && r < 2.0; // Ensuring precondition for monotonicSquare
    monotonicSquare(r, 2.0); 
  }
}

lemma monotonicMult(c: real, x: real, y: real)
  requires x < y && c > 0.0
  ensures c * x < c * y
{}

lemma monotonicSquare(x: real, y: real)
  requires 0.0 < x < y
  ensures 0.0 < x * x < y * y
{
    monotonicMult(x, x, y);
