  var j := a.Length;
  var k := a.Length;

  while i < j
    invariant 0 <= i <= j <= k <= a.Length
    invariant forall x | 0 <= x < i :: a[x] < 0
    invariant forall x | i <= x < j :: a[x] == 0
    invariant forall x | j <= x < k :: a[x] > 0
    invariant forall x | k <= x < a.Length :: a[x] > 0
    decreases j - i
