method Partition(a: array<int>, lo: int, hi: int) returns (p: int)
    requires 0 <= lo < hi <= a.Length
    requires SplitPoint(a, lo) && SplitPoint(a, hi)
    modifies a
    ensures lo <= p < hi
    ensures forall i :: lo <= i < p ==> a[i] < a[p]
    ensures forall i :: p < i < hi ==> a[p] <= a[i]
    ensures SplitPoint(a, lo) && SplitPoint(a, hi)
    ensures SwapFrame(a, lo, hi)
{
    var pivot := a[lo];
    var m := lo + 1;
    var n := hi;
    while m < n
        invariant lo + 1 <= m <= n <= hi
        invariant forall i :: lo < i < m ==> a[i] < pivot
        invariant forall i :: n <= i < hi ==> pivot <= a[i]
        invariant a[lo] == pivot
        invariant multiset(a[lo..hi]) == old(multiset(a[lo..hi]))
        decreases n - m
    {
        if a[m] < pivot {
            m := m + 1;
        } else {
            a[m], a[n-1] := a[n-1], a[m];
            n := n - 1;
        }
    }
    a[lo], a[m - 1] := a[m - 1], a[lo];
    return m - 1;
}

