  ensures res == reverseList(head)
{
  var prev: ListNode := Null;
  var curr: ListNode := head;
  while curr != Null
    decreases curr
    invariant reverseList(nodeConcat(reverseList(prev), curr)) == head
  {
    var next: ListNode := curr.next;
    curr.next := prev; // Correctly updating the next field
    prev := curr;
    curr := next;
  }
  res := prev;
}

