  var i := 0;
  while (i < d.Length)
    invariant 0 <= i <= d.Length
    invariant forall j :: 0 <= j < i ==> d[j] == -1
  {
    d[i] := -1;
    i := i + 1;
  }

  i, p, q := 0, -1, -1; // Initialize p and q to -1 indicating no duplicates found yet
  while (i < a.Length)
    invariant 0 <= i <= a.Length
    invariant forall j :: 0 <= j < a.Length-2 ==> 
                (d[j] == -1 && forall k :: 0 <= k < i ==> a[k] != j) ||
                (0 <= d[j] < i && a[d[j]] == j)
    invariant p == -1 || IsPrefixDuplicate(a, i, p)
    invariant q == -1 || IsPrefixDuplicate(a, i, q)
    invariant p != -1 && q != -1 ==> p != q && IsDuplicate(a, p) && IsDuplicate(a, q)
    decreases a.Length - i
  {
    var current := a[i];
    if (d[current] == -1) {
      d[current] := i; // Mark the first occurrence of current
    } else {
      if (p == -1) {
        p := current; // First duplicate found
      } else if (q == -1 && p != current) {
        q := current; // Second distinct duplicate found
        if (IsDuplicate(a, p) && IsDuplicate(a, q)) {
          break; // Found both duplicates, can break
        }
