method mergeArr(a: array<int>, l: int, m: int, r: int)
  requires 0 <= l < m < r <= a.Length
  requires sorted(a[l..m]) && sorted(a[m..r])
  ensures sorted(a[l..r])
  ensures a[..l] == old(a[..l])
  ensures a[r..] == old(a[r..])
  modifies a
{
  var left := copyArr(a, l, m);
  var right := copyArr(a, m, r);
  var i := 0;
  var j := 0;
  var cur := l;

  while cur < r
    invariant l <= cur <= r
    invariant 0 <= i <= left.Length
    invariant 0 <= j <= right.Length
    invariant sorted(a[l..cur])
    invariant a[..l] == old(a[..l])
    invariant a[r..] == old(a[r..])
    invariant forall k :: l <= k < cur && k < m + i ==> a[k] == left[k - l]
    invariant forall k :: m + j <= k < cur ==> a[k] == right[k - m]
    decreases r - cur
  {
    if (i < left.Length && (j >= right.Length || left[i] <= right[j])) {
      a[cur] := left[i];
      i := i + 1;
    } else if (j < right.Length) {
      a[cur] := right[j];
      j := j + 1;
    }
    cur := cur + 1;
  }
  return;
}

