method FindAddends(q: seq<int>, x: int) returns (i: nat, j: nat)
	requires Sorted(q) && HasAddends(q, x)
	ensures i < j < |q| && q[i]+q[j] == x
{
	i := 0;
	j := |q| - 1;
	var sum := q[i] + q[j];

	while sum != x
		invariant 0 <= i < j < |q|
		invariant exists k, l :: 0 <= k < l < |q| && q[k] + q[l] == x
		invariant forall k, l :: i <= k < l <= j ==> q[k] + q[l] != x || (k == i && l == j)
		invariant sum != x
		decreases j - i
	{
		if (sum > x)
		{
			j := j - 1;
		}
		else // (sum < x)
		{
			i := i + 1;
		}
		sum := q[i] + q[j];
	}
}

