  invariant 0 <= a <= n;
  invariant forall i :: 0 <= i < a ==> X[i] <= p;
  invariant multiset(X[..]) == multiset(old(X[..]));
  while (a < n && X[a] <= p) 
  { 
    a := a + 1;
  }

  b := a;

  // Loop invariants for the second loop
  invariant a <= b <= n;
  invariant forall i :: a <= i < b ==> X[i] > p;
  invariant multiset(X[..]) == multiset(old(X[..]));
  while (b < n)
  { 
