ghost predicate correct_pair(pair: (int, int), nums: seq<int>, target: int) {
  var (i, j) := pair;
  && 0 <= i < |nums|
  && 0 <= j < |nums|
  && i != j  // "you may not use the same element twice"
  && nums[i] + nums[j] == target
}

// We actually make a weaker pre-condition: there exists at least one solution.
// For verification simplicity, we pretend as if:
// - `seq` were Python list
// - `map` were Python dict
method twoSum(nums: seq<int>, target: int) returns (pair: (int, int))
  requires exists i, j :: correct_pair((i, j), nums, target)
  ensures correct_pair(pair, nums, target)
{
  var e_to_i := map[];

  for j := 0 to |nums| - 1
    invariant forall k :: 0 <= k < j ==> (nums[k] in e_to_i ==> e_to_i[nums[k]] == k)
    invariant forall k :: 0 <= k < j ==> (target - nums[k] in e_to_i ==> e_to_i[target - nums[k]] < k)
    invariant forall k :: 0 <= k < j ==> (target - nums[k] in e_to_i ==> nums[e_to_i[target - nums[k]]] + nums[k] != target)
    invariant forall k :: 0 <= k < j ==> (target - nums[k] in e_to_i ==> correct_pair((e_toi[target - nums[k]], k), nums, target))
  {
    var element := nums[j];
    var rest := target - element;
    if rest in e_to_i {  // partner found!
      var i := e_to_i[rest];
      assert i < j;  // Ensuring index i is less than j
      assert nums[i] + nums[j] == target;  // Ensuring the sum of elements at indices i and j equals target
      assert correct_pair((i, j), nums, target);  // Asserting that the pair found is correct
      return (i, j);
    } else {
      e_to_i := e_to_i[element := j];
    }
  }
  // unreachable here, since there's at least one solution
}

