// RUN: %dafny /compile:0 "%s" > "%t"
// RUN: %diff "%s.expect" "%t"

// Spec# and Boogie and Chalice:  The program will be
// the same, except that these languages do not check
// for any kind of termination.  Also, in Spec#, there
// is an issue of potential overflows.

// Benchmark1

method Add(x: int, y: int) returns (r: int)
  ensures r == x + y;
{
  r := x;
  if (y < 0) {
    var n := y;
    while (n != 0)
      invariant r + n == x + y
      decreases -n // Ensuring a positive decreases clause for negative y
    {
      r := r - 1;
      n := n + 1;
    }
  } else {
    var n := y;
    while (n != 0)
      invariant r + n == x + y
      decreases n // Positive decreases clause for positive y
    {
      r := r + 1;
      n := n - 1;
    }
  }
}

method Mul(x: int, y: int) returns (r: int)
  ensures r == x * y;
{
  if (x == 0) {
    r := 0;
  } else if (x < 0) {
    r := Mul(-x, y);
    r := -r;
  } else {
    r := 0;
    var i := 0;
    while (i < x)
      invariant r == i * y
      invariant 0 <= i <= x
      decreases x - i // Ensuring a positive decreases clause
    {
      r := Add(r, y);
      i := i + 1;
    }
  }
}

