method Nice(n: int) returns (k: int) {
  var f : int -> int := x => x;
  var i := new Ref<int>(0);
  while i.val < n
    invariant i.val <= n
    invariant forall x :: f(x) == x + i.val
  {
    i.val := i.val + 1;
    f := x => f(x) + 1;
  }
  return f(0);
}

method OneShot(n: int) returns (k: int) {
  var f : int -> int := x => x;
  var i := 0;
  while i < n
    invariant i <= n
    invariant forall x :: f(x) == x + i
  {
    i := i + 1;
    f := x => f(x) + 1;
  }
  k := f(0);
}

method HeapQuant(n: int) returns (k: int) {
  var f : int -> int := x => x;
  var i := new Ref<int>(0);
  ghost var r := 0;
  while i.val < n
    invariant i.val <= n
    invariant r == i.val
    invariant forall x :: f(x) == x + r
  {
    i.val, r := i.val + 1, r + 1;
    f := x => f(x) + 1;
  }
  k := f(0);
}

