        var length := 0;
        for j := i to |s| - 1
            invariant i <= j <= |s|
            invariant sum >= 0
            invariant length == j - i
            invariant forall k :: i <= k < j ==> IsDigit(s[k])
            invariant sum == sum + (s[j] as int - 48) * (j - i)
