  }

  predicate WellformedINP(c: Circuit, inp: INodePort) {
    inp.node_id < c.nodes.Length && inp.port_id < n_iports(c.nodes[inp.node_id])
  }

  predicate WellformedONP(c: Circuit, onp: ONodePort) {
    onp.node_id < c.nodes.Length && onp.port_id < n_oports(c.nodes[onp.node_id])
