datatype Bases = A | C | G | T

method Exchanger(s: seq<Bases>, x: nat, y: nat) returns (t: seq<Bases>)
  requires 0 < |s| && x < |s| && y < |s|
  ensures |t| == |s|
  ensures forall b: nat :: 0 <= b < |s| && b != x && b != y ==> t[b] == s[b]
  ensures t[x] == s[y] && s[x] == t[y]
  ensures multiset(s) == multiset(t)
{
  t := s;
  t := t[x := s[y]];
  t := t[y := s[x]];
  return t;
}

predicate below(first: Bases, second: Bases)
{
  first == second ||
  first == A || 
  (first == C && (second == G || second == T)) || 
  (first == G && second == T)
}

predicate bordered(s: seq<Bases>)
{
  forall j, k :: 0 <= j < k < |s| ==> below(s[j], s[k])
}

method Sorter(bases: seq<Bases>) returns (sobases: seq<Bases>)
  requires 0 < |bases|
  ensures |sobases| == |bases|
  ensures bordered(sobases)
  ensures multiset(bases) == multiset(sobases)
{
  sobases := bases;
  var c, next: nat := 0, 0;
  var g, t: nat := |bases|, |bases|;

  while next != g
    invariant 0 <= c <= next <= g <= t <= |sobases|
    invariant multiset(sobases) == multiset(bases)
    invariant forall i: nat :: 0 <= i < c ==> sobases[i] == A
    invariant forall i: nat :: c <= i < next ==> sobases[i] == C
    invariant forall i: nat :: next <= i < g ==> sobases[i] in {G, T}
    invariant forall i: nat :: g <= i < t ==> sobases[i] == T
    invariant forall i: nat :: t <= i < |sobases| ==> sobases[i] == T
    invariant bordered(sobases[0..c]) && bordered(sobases[c..next]) && bordered(sobases[next..g]) && bordered(sobases[g..t]) && bordered(sobases[t..|sobases|])
    invariant forall i: nat, j: nat :: 0 <= i < j < |sobases| ==> below(sobases[i], sobases[j])
  {
    match sobases[next] {
      case C =>
        next := next + 1;
      case A =>
        sobases := Exchanger(sobases, next, c);
        c, next := c + 1, next + 1;
      case G =>
        g := g - 1;
        sobases := Exchanger(sobases, next, g);
      case T =>
        g, t := g - 1, t - 1;
        sobases := Exchanger(sobases, next, t);
        if (g != t) {
          sobases := Exchanger(sobases, next, g);
        }
    }
  }

  return sobases;
}

