                i := i + 1;
            }
            j := j + 1;
        }
        intSeq[hi - 1] := intSeq[i];
        intSeq[i] := pivot;

        quickSortHelper(intSeq, lo, i);
        quickSortHelper(intSeq, i + 1, hi);
