// e ::= x | x : .e | true| false| e1 e2 | if e then e1 else e2
// v ::= true | false | x : .e
// E ::= [] | E e | v E | if E then e1 else e2
type VarName = string

datatype Type = Int | Bool | Func(dom: Type, codom: Type)

datatype Exp =
    | Var(x: VarName)
    | Lam(x: VarName, t: Type, e: Exp)
    | App(e1: Exp, e2: Exp)
    | True()
    | False()
    | Cond(e0: Exp, e1: Exp, e2: Exp)

datatype Value =
    | TrueB()
    | FalseB()
    | LamV(x: VarName, t: Type, e: Exp)

datatype Eva = 
    | E()
    | EExp(E : Eva, e : Exp)
    | EVar(v : Value, E : Eva)
    | ECond(E:Eva, e1: Exp, e2: Exp)

