        case Var(x) => {x}
        case Lam(x, t, e) => FV(e) - {x}
        case App(e1, e2) => FV(e1) + FV(e2)
        case True() => {}
        case False() => {}
        case Cond(e0, e1, e2) => FV(e0) + FV(e1) + FV(e2)
