      assert |cp| == 0;
    } else {
      var x :| x in setA;
      var setA' := setA - {x};
      var cp' := (set a, b | a in setA' && b in setB :: (a,b));
      var line := (set a, b | a == x && b in setB :: (a,b));
      assert |line| == |setB|; // Each element in setB pairs with x to form a tuple in line
      CrossProductCardinality(setA', setB, cp');
      assert |cp'| == |setA'| * |setB|; // Inductive hypothesis
      assert |cp| == |cp'| + |line|; // cp is the union of cp' and line
      assert |cp| == (|setA| - 1) * |setB| + |setB|; // Arithmetic calculation
      assert |cp| == |setA| * |setB|; // Final equality
    }
  }
}

